package com.jeremyhaberman.raingauge.test;import android.test.suitebuilder.annotation.SmallTest;import com.jeremyhaberman.raingauge.Service;import com.jeremyhaberman.raingauge.ServiceManager;import junit.framework.TestCase;public class ServiceManagerTest extends TestCase {	@SmallTest	public void testLoadService() {		ServiceManager serviceManager = ServiceManager.createServiceManager();		ServiceManager.load(serviceManager);		assertEquals(serviceManager, ServiceManager.get());	}	@SmallTest	public void testGetServiceBeforeInitialization() {		ServiceManager.load(null);		try {			ServiceManager.getService(Service.REST_METHOD_FACTORY);			fail("Should have thrown IllegalArgumentException");		} catch (IllegalArgumentException e) {			assertTrue(true);		}	}}